import React, {useEffect,useState} from 'react';
import Navbar from "react-bootstrap/Navbar";
import Table from 'react-bootstrap/Table';
import Pagination from 'react-bootstrap/Pagination';
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import axios from 'axios';

const UserListPage =()=>{
    const [data, setData] = useState([]);
    let active = 1;
    let items = [];
    const paginationClickHandler=(e)=>{
        console.log(e.target.text);
        fetchData(parseInt(e.target.text));
    };

    for (let number = 0; number <= 1000; number+=50) {
        items.push(
            <Pagination.Item onClick={paginationClickHandler} key={number} active={number === active}>
                {number}
            </Pagination.Item>,
        );
    }

    const fetchData=(startIndex)=>{
            axios.get(`http://localhost:5000/users/${startIndex}`)
                .then(res => {
                    setData(res.data.users);
                });
        };

    useEffect(() => {
        fetchData(0);
    }, []);


    return (
        <>
            <Navbar bg="primary" variant="dark">
                <Navbar.Brand href="#home">Navbar</Navbar.Brand>
            </Navbar>
            <h1>User Statistics</h1>
            <Table responsive>
                <thead>
                <tr>
                    <th>id</th>
                    <th>first_name</th>
                    <th>last_name</th>
                    <th>email</th>
                    <th>gender</th>
                    <th>ip_address</th>
                    <th>total_clicks</th>
                    <th>total_page_views</th>
                </tr>
                </thead>
                <tbody>
                {data.map((user)=>{
                    return (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.first_name}</td>
                            <td>{user.last_name}</td>
                            <td>{user.email}</td>
                            <td>{user.gender}</td>
                            <td>{user.ip_address}</td>
                            <td>{user.total_clicks}</td>
                            <td>{user.total_page_views}</td>
                        </tr>
                        )
                })}
                </tbody>
            </Table>
            <Pagination size="sm">{items}</Pagination>
            <Navbar bg="primary" variant="dark">
                <Navbar.Brand href="#home">Navbar</Navbar.Brand>
            </Navbar>
        </>
    )
};

export default  UserListPage;
